<div class=" highlight hl-ipython3"><pre><span></span><span class="n">football</span><span class="o">.</span><span class="n">eval</span><span class="p">(</span><span class="n">name</span> <span class="o">=</span> <span class="s2">&quot;year&quot;</span><span class="p">,</span>
              <span class="n">expr</span> <span class="o">=</span> <span class="s2">&quot;YEAR(date)&quot;</span><span class="p">)</span>
<span class="n">football</span><span class="o">.</span><span class="n">analytic</span><span class="p">(</span><span class="s1">&#39;row_number&#39;</span><span class="p">,</span> 
                  <span class="n">order_by</span> <span class="o">=</span> <span class="p">{</span><span class="s2">&quot;date&quot;</span><span class="p">:</span> <span class="s2">&quot;desc&quot;</span><span class="p">},</span> 
                  <span class="n">by</span> <span class="o">=</span> <span class="p">[</span><span class="s2">&quot;tournament&quot;</span><span class="p">,</span> <span class="s2">&quot;year&quot;</span><span class="p">]</span> <span class="p">,</span> 
                  <span class="n">name</span> <span class="o">=</span> <span class="s2">&quot;order_tournament&quot;</span><span class="p">)</span>
